//! [438]找到字符串中所有字母异位词

pub fn find_anagrams(s: String, p: String) -> Vec<i32> {
    let s = s.as_bytes();
    let p = p.as_bytes();
    let target_len = p.len();
    let len = s.len();
    if target_len > len {
        return vec![];
    }
    let mut result = vec![];
    let mut cur = [0; 26];
    let mut target = [0; 26];

    for i in 0..target_len {
        target[(p[i] - b'a') as usize] += 1;
        cur[(s[i] - b'a') as usize] += 1;
    }
    for i in target_len..=len {
        if cur == target {
            result.push((i - target_len) as i32);
        }
        if i == len {
            break;
        }
        cur[(s[(i - target_len)] - b'a') as usize] -= 1;
        cur[(s[i] - b'a') as usize] += 1;
    }
    result
}


fn main() {
    let a = String::from("");
    let b = String::from("yqrbgjdwtcaxzsnifvhmou");
    println!("{:?}", find_anagrams(a, b));
    println!("{:?}", find_anagrams(String::from("abab"), String::from("ab")));
    println!("{:?}", find_anagrams(String::from("cbaebabacd"), String::from("abc")));
}

